import './App.css';
import React from 'react';
import LetterBox from './LetterBox';
import SingleLetterSearchbar from './SingleLetterSearchBar';

const pics = ['noose.png', 'upperBody.png', 'upperandlower.png', '1arm.png', 'botharms.png'];
const words = ["Morehouse", "Spelman", "Basketball", "Table", "Museum", "Excellent", "Fun", "React"];

class HangmanGame extends React.Component {
  state = {
    wordList: [],
    curWord: 0,
    lifeLeft: 0,
    usedLetters: [],
    revealedLetters: []
  };

  componentDidMount() {
    this.setState({ wordList: words });
  }

  getPlayerName = (name) => {
    this.setState({ playerName: name });
  };

  startNewGame = () => {
    const randomIndex = Math.floor(Math.random() * words.length);
    const newWord = words[randomIndex];
    this.setState({
      curWord: randomIndex,
      lifeLeft: 0, // reset to noose.png
      usedLetters: [],
      revealedLetters: Array(newWord.length).fill(false)
    });
  };

  guess = (letter) => {
    const { curWord, wordList, usedLetters, lifeLeft, revealedLetters } = this.state;
    const word = wordList[curWord]?.toLowerCase();
    const lowerLetter = letter.toLowerCase();

    if (!word || usedLetters.includes(lowerLetter)) return;

    let newUsedLetters = [...usedLetters, lowerLetter];
    let newRevealed = [...revealedLetters];
    let newLife = lifeLeft;

    if (word.includes(lowerLetter)) {
      for (let i = 0; i < word.length; i++) {
        if (word[i] === lowerLetter) newRevealed[i] = true;
      }
    } else {
      newLife = Math.min(pics.length - 1, lifeLeft + 1);
    }

    this.setState({
      usedLetters: newUsedLetters,
      revealedLetters: newRevealed,
      lifeLeft: newLife
    });
  };

  render() {
    const { wordList, curWord, lifeLeft, revealedLetters, usedLetters } = this.state;
    const word = wordList[curWord];
    const displayedWord = word ? word.split('').map((l, i) => (revealedLetters[i] ? l : '_')).join(' ') : '';

    return (
      <div>
        <img src={`${process.env.PUBLIC_URL}/${pics[lifeLeft]}`} alt="hangman" />
        <button onClick={this.startNewGame}>New Game</button>
        <p style={{ fontSize: '24px' }}>{displayedWord}</p>
        <SingleLetterSearchbar onSearch={this.guess}></SingleLetterSearchbar>
        <p>Used Letters: {usedLetters.join(', ')}</p>
        <LetterBox
          letter="a"
          isVisible={true}
          boxStyle={{ backgroundColor: 'lightblue' }}
          letterStyle={{ color: 'white', fontSize: '30px' }}
        ></LetterBox>
      </div>
    );
  }
}

export default HangmanGame;
