import './App.css';
import React from 'react';
import LetterBox from './LetterBox';
import SingleLetterSearchbar from './SingleLetterSearchBar';

// ðŸª¢ These files should all be in your "public/" folder
const pics = [
  'noose.png',
  'upperBody.png',
  'upperandlower.png',
  '1arm.png',
  'botharms.png',
  '1leg.png',
  'Dead.png'
];

const words = [
  "Morehouse",
  "Spelman",
  "Basketball",
  "Table",
  "Museum",
  "Excellent",
  "Fun",
  "React"
];

class HangmanGame extends React.Component {
  state = {
    wordList: [],
    curWord: 0,
    lifeLeft: 0,
    usedLetters: [],
    revealedLetters: []
  };

  componentDidMount() {
    this.setState({
      wordList: words
    });
  }

  getPlayerName = (name) => {
    this.setState({
      playerName: name
    });
  };

  // ðŸ†• Always reset to show the noose image first
  startNewGame = () => {
    const randomIndex = Math.floor(Math.random() * this.state.wordList.length);
    const selectedWord = this.state.wordList[randomIndex];

    this.setState({
      curWord: randomIndex,
      lifeLeft: 0,
      usedLetters: [],
      revealedLetters: new Array(selectedWord.length).fill(false)
    });
  };

  // ðŸ”  Guess function that checks if letter exists in the word
  guess = (letter) => {
    const { curWord, wordList, usedLetters, lifeLeft, revealedLetters } = this.state;
    const word = wordList[curWord]?.toLowerCase();
    const lowerLetter = letter.toLowerCase();

    if (!word || usedLetters.includes(lowerLetter)) return; // ignore repeats or empty word

    let newUsedLetters = [...usedLetters, lowerLetter];
    let newRevealed = [...revealedLetters];
    let newLife = lifeLeft;

    if (word.includes(lowerLetter)) {
      for (let i = 0; i < word.length; i++) {
        if (word[i] === lowerLetter) newRevealed[i] = true;
      }
    } else {
      //  Wrong guess â€” lose a life
      newLife = Math.min(pics.length - 1, lifeLeft + 1);
    }

    this.setState({
      usedLetters: newUsedLetters,
      revealedLetters: newRevealed,
      lifeLeft: newLife
    });
  };

  render() {
    const { wordList, curWord, revealedLetters, lifeLeft } = this.state;
    const word = wordList[curWord] || "";

    // Show underscores and revealed letters
    const displayWord = word
      .split("")
      .map((letter, index) => (revealedLetters[index] ? letter : "_"))
      .join(" ");

    return (
      <div style={{ textAlign: "center" }}>
        <img
          src={`/${pics[lifeLeft]}`}  // âœ… pulls from public folder
          alt="hangman"
          style={{ width: "200px", marginBottom: "20px" }}
        />
        <br />

        <button onClick={this.startNewGame} style={{ marginBottom: "10px" }}>
          New Game
        </button>

        <p style={{ fontSize: "24px", letterSpacing: "5px" }}>{displayWord}</p>

        <SingleLetterSearchbar onSearch={this.guess} />

        <div style={{ marginTop: "10px" }}>
          <p>Used Letters: {this.state.usedLetters.join(", ")}</p>
          <p>Lives Left: {pics.length - 1 - lifeLeft}</p>
        </div>

        <LetterBox
          letter="a"
          isVisible={true}
          boxStyle={{ backgroundColor: 'lightblue' }}
          letterStyle={{ color: 'white', fontSize: '30px' }}
        />
      </div>
    );
  }
}

export default HangmanGame;
