import React, { useState } from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import HangmanGame from './HangmanGame';
import reportWebVitals from './reportWebVitals';
import PlayerLogin from './PlayerLogin';
import { updatePlayerStats } from './api';

function Main() {
  const [player, setPlayer] = useState(null);

  // called by HangmanGame when game ends
  async function handleGameOver(didWin) {
    if (!player) return; // no one logged in, nothing to track

    const newWins = player.wins + (didWin ? 1 : 0);
    const newLosses = player.losses + (didWin ? 0 : 1);
    const total = newWins + newLosses;
    const winPercentage = total ? Math.round((newWins / total) * 100) : 0;

    // update info in React
    const updatedPlayer = {
      ...player,
      wins: newWins,
      losses: newLosses,
      winPercentage,
    };
    setPlayer(updatedPlayer);

    // sync to DynamoDB via API
    try {
      await updatePlayerStats(player.playerName, newWins, newLosses);
    } catch (e) {
      console.error('Failed to update stats', e);
    }
  }

  return (
    <>
      {!player && <PlayerLogin onLogin={setPlayer} />}

      {player && (
        <div data-testid="player-stats">
          <p><strong>Player:</strong> {player.playerName}</p>
          <p><strong>Wins:</strong> {player.wins}</p>
          <p><strong>Losses:</strong> {player.losses}</p>
          <p><strong>Win %:</strong> {player.winPercentage}%</p>
        </div>
      )}

      {/* Game stays the same, just given a callback */}
      <HangmanGame onGameOver={handleGameOver} />
    </>
  );
}

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <Main />
  </React.StrictMode>
);

