import './App.css';
import React from 'react';
import LetterBox from './LetterBox';
import SingleLetterSearchbar from './SingleLetterSearchBar';

// ✅ Your original format — images live in /public/
const pics = [
  'noose.png',
  'upperBody.png',
  'upperandlowerbody.png',
  '1arm.png',
  'botharms.png',
  '1leg.png',
  'Dead.png'
];

const words = [
  "Morehouse",
  "Spelman",
  "Basketball",
  "Table",
  "Museum",
  "Excellent",
  "Fun",
  "React"
];

class HangmanGame extends React.Component {
  state = {
    wordList: [],
    curWord: 0,
    lifeLeft: 0,
    usedLetters: [],
    revealedLetters: []
  };

  componentDidMount() {
    this.setState({
      wordList: words
    });
  }

  startNewGame = () => {
    const randomIndex = Math.floor(Math.random() * words.length);
    const newWord = words[randomIndex];
    this.setState({
      curWord: randomIndex,
      lifeLeft: 0, // resets to show noose.png
      usedLetters: [],
      revealedLetters: Array(newWord.length).fill(false)
    });
  };

  guess = (letter) => {
    const { curWord, wordList, usedLetters, lifeLeft, revealedLetters } = this.state;
    const word = wordList[curWord]?.toLowerCase();
    const lowerLetter = letter.toLowerCase();

    if (!word || usedLetters.includes(lowerLetter)) return; // ignore repeats or invalid words

    let newUsedLetters = [...usedLetters, lowerLetter];
    let newRevealed = [...revealedLetters];
    let newLife = lifeLeft;

    // ✅ Check if guess is correct or not
    if (word.includes(lowerLetter)) {
      for (let i = 0; i < word.length; i++) {
        if (word[i] === lowerLetter) newRevealed[i] = true;
      }
    } else {
      // ❌ Wrong guess — advance to next image in order
      newLife = Math.min(pics.length - 1, lifeLeft + 1);
    }

    this.setState({
      usedLetters: newUsedLetters,
      revealedLetters: newRevealed,
      lifeLeft: newLife
    });
  };

  render() {
    const { wordList, curWord, revealedLetters, lifeLeft, usedLetters } = this.state;
    const word = wordList[curWord] || "";

    // build display like "_ _ _ _"
    const displayedWord = word
      .split("")
      .map((letter, i) => (revealedLetters[i] ? letter : "_"))
      .join(" ");

    return (
      <div style={{ textAlign: 'center', marginTop: '20px' }}>
        {/* ✅ Since images are in public/, prefix with process.env.PUBLIC_URL */}
        <img
          src={`${process.env.PUBLIC_URL}/${pics[lifeLeft]}`}
          alt="hangman"
          style={{ width: '200px', height: 'auto' }}
        />

        <div>
          <button
            onClick={this.startNewGame}
            style={{ margin: '10px', padding: '10px', fontSize: '16px' }}
          >
            New Game
          </button>
        </div>

        <p style={{ fontSize: '28px', letterSpacing: '4px' }}>{displayedWord}</p>

        <SingleLetterSearchbar onSearch={this.guess} />

        <div style={{ marginTop: '20px', fontSize: '18px' }}>
          <p>Used Letters: {usedLetters.join(", ")}</p>
        </div>

        {/* Example visual letter box */}
        <LetterBox
          letter="A"
          isVisible={true}
          boxStyle={{ backgroundColor: 'lightblue' }}
          letterStyle={{ color: 'white', fontSize: '30px' }}
        />
      </div>
    );
  }
}

export default HangmanGame;
